    const response = await fetch(`https://api.game.com/players/${id}`, {
        method: 'DELETE'
    });

    return response.ok;
}

// Uso
const eliminado = await deletePlayer('charlie');
if (eliminado) {
    console.log('Jugador eliminado correctamente');
}






