    // Resetear velocidad
    this.jugador.setVelocityX(0);

    // Comprobar teclas presionadas
    if (this.cursors.left.isDown) {
        this.jugador.setVelocityX(-160);
    } else if (this.cursors.right.isDown) {
        this.jugador.setVelocityX(160);
